const handleSubmit = (event) => {
  event.preventDefault();
  if (!username || !password || !confirmPassword || !email) {
    setValidationMessage('Please fill in all fields');
    return;
  } else if (password !== confirmPassword) {
    setValidationMessage('Passwords do not match');
    return;
  } else if (!validatePassword(password)) {
    setValidationMessage('Password must contain at least one uppercase letter, one lowercase letter, and one number.');
    return;
  }

  const loginData = {
    username: username,
    password: password,
    email: email,
  };

  // Send loginData to the backend
  fetch('http://127.0.0.1:5000/login', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(loginData),
  })
    .then((response) => response.json())
    .then((data) => {
      console.log('Success:', data);
      // Navigate to homepage-2 after successful login
      navigateTo('/homepage-2');
    })
    .catch((error) => {
      console.error('Error:', error);
      // Handle login error
    });
};
