import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect,
} from 'react-router-dom';

import './style.css';

import Login from './views/login';
import Register from './views/register';
import PrepareMeal from './views/prepare-meal';
import Profile from './views/profile-page';
import NavBar from './views/nav-bar';

const App = () => {
  // Dark Mode State
  const [isDarkMode, setIsDarkMode] = useState(() => {
    return localStorage.getItem("darkMode") === "true";
  });

  // Function to toggle dark mode
  const toggleDarkMode = () => {
    setIsDarkMode(prevMode => {
      const newMode = !prevMode;
      localStorage.setItem("darkMode", newMode);
      return newMode;
    });
  };

  return (
    <Router>
      {/* Apply dark mode globally */}
      <div className={isDarkMode ? "dark-mode" : ""}>
        {/* Pass isDarkMode & toggleDarkMode to NavBar */}
        <NavBar isDarkMode={isDarkMode} toggleDarkMode={toggleDarkMode} />

        <Switch>
          <Route component={Login} exact path="/" />
          <Route component={Login} exact path="/login" />
          <Route component={Register} exact path="/register" />
          <Route component={Profile} exact path="/profile-page" />
          <Route component={() => <PrepareMeal isDarkMode={isDarkMode} />} exact path="/prepare-meal" />
          <Route component={Login} exact path="/home-page2" />
          <Route component={NavBar} exact path="/nav" />

          <Redirect to="**" />
        </Switch>
      </div>
    </Router>
  );
};

ReactDOM.render(<App />, document.getElementById('app'));
