import React, { Component } from 'react';
import { Helmet } from 'react-helmet';
import { useHistory } from 'react-router-dom';
import './register.css';

class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      password: '',
      confirmPassword: '',
      email: '',
      error: ''
    };
  }

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }

  handleSubmit = (event) => {
    event.preventDefault();
    const { username, password, confirmPassword, email } = this.state;
    const { history } = this.props; // Access history prop

    if (!username || !password || !confirmPassword || !email) {
      this.setState({ error: 'Please fill in all fields' });
    } else if (password !== confirmPassword) {
      this.setState({ error: 'Passwords do not match' });
    } else {
      const newUser = { username, password, email };

      fetch("http://127.0.0.1:5000/register", {
        method: "POST",
        body: JSON.stringify(newUser),
        headers: {
          "Content-type": "application/json; charset=UTF-8"
        }
      })
        .then((response) => response.json())
        .then((json) => {
          console.log(json);
          if (json.error) {
            this.setState({ error: json.error });
          } else {
            history.push('/home-page2');  // Use history to redirect
          }
        });
    }
  }

  render() {
    const { username, password, email, confirmPassword, error } = this.state;

    return (
      <div className="register-container">
        <Helmet>
          <title>exported project</title>
        </Helmet>
        <form onSubmit={this.handleSubmit}>
          <div className="register-input-login">
            <span className="ConfirmPass">Confirm Password:</span>
            <input
              className="PasswordInput"
              type="password"
              placeholder="Enter your Password"
              value={confirmPassword}
              onChange={this.handleInputChange}
              name="confirmPassword"
            />
            <span className="EnterPass">Enter Password:</span>
            <input
              className="PasswordInput2"
              type="password"
              placeholder="Re-Enter your Password"
              value={password}
              onChange={this.handleInputChange}
              name="password"
            />
            <span className="EnterEmail">Enter Email:</span>
            <input
              className="regEmail"
              type="email"
              placeholder="Enter your Email"
              value={email}
              onChange={this.handleInputChange}
              name="email"
            />
            <span className="EnterUsername">Enter New Username:</span>
            <input
              className="regUsername"
              type="text"
              placeholder="Enter your Username"
              value={username}
              onChange={this.handleInputChange}
              name="username"
            />
          </div>

          {error && <p className="validation-message">{error}</p>}

          <button type="submit" className="RegisterButton">
            <span className="register-text37">Register</span>
          </button>
        </form>
      </div>
    );
  }
}

// Wrap the component with useNavigate hook
const RegisterWithHistory = (props) => {
  const history = useHistory();
  return <Register {...props} history={history} />;
};

export default RegisterWithHistory;
